//import { useForm } from "react-hook-form";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import validator from 'validator';
//import axios from "axios";
import "./CSS/register.css";


const Registration = () => {
  const history = useHistory();
  const [emailError, setEmailError] = useState('')
  const [ user, setUser] = useState({
    email:"",
    password:""

});



const handleChange = e => {
    const { name, value } = e.target
    setUser({
        ...user,
        [name]: value
    })
}

const handleSubmitbtn = async (e) => {
  e.preventDefault();
    const { email, password } = user;

    if (validator.isEmail(email)) {
      setEmailError('Valid Email :)')
    } else {
      setEmailError('Enter valid Email!')
    }
    const res = await fetch('/signup',{
      method: "POST",
      headers: {"Content-Type" : "application/json"},
      body: JSON.stringify({email, password})
    });

    const data = await res.json();
    history.push("/login");

    if(data.status === 400 || !data) {
      alert("Invali registr");
      console.log("Invali registr");
    }
    else {
      alert("succesfull registr");
      console.log("succesfull registr");
    }


}

  return (
    <div>

        <form  method="POST">
          <input type="text" name="email" value={user.email} 
          placeholder="Your Email" onChange={ handleChange } 

          />
           <p>{emailError}</p>
            <input type="password" name="password" value={user.password} 
            placeholder="Your Password" onChange={ handleChange } 

            />

            <button className="button" onClick={handleSubmitbtn} >Register</button>
          </form>
          <button className="button" onClick={() => history.push("/login")}>Login</button>
    </div>
  );
}

export default Registration;
