import { useForm } from "react-hook-form";

import "./CSS/register.css";
const Registration = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors }
  } = useForm();

  const onSubmit = (data) => {
     fetch('mongodb://localhost/signup',{
       method : 'Post',
       headers:{'content-type' : 'application/Json'},
       body : JSON.stringify(data)
     }).then(() =>{
       console.log("new user added");
     })
    //alert("welcome succuefully register !!");
    alert(JSON.stringify(data));
  }; // your form submit function which will invoke after successful validation

  console.log(watch("example")); // you can watch individual input by pass the name of the input

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <label>Full Name</label>
      <input
        {...register("fulltName", {
          required: true,
          minLength:3,
          maxLength: 100,
          pattern: /^[A-Za-z]+$/i
        })}
      />
      {errors?.fullName?.type === "required" && <p>This field is required</p>}
      {errors?.firstName?.type === "maxLength" && (
        <p>Full name cannot exceed 100 characters</p>
      )}
      {errors?.fullName?.type === "pattern" && (
        <p>Alphabetical characters only</p>
      )}
      <label>Email id</label>
      <input {...register("email", { pattern:  /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i })} />
      {errors?.email?.type === "pattern" && (
        <p>Enetr Valid Email</p>
      )}
      <label>Password</label>
      <input

        {...register("password",{
          required: "You must specify a password",
          minLength: {
            value: 8,
            message: "Password must have at least 8 characters"
          }
        })}
      />
      {errors.password && <p>{errors.password.message}</p>}

      <input type="submit" />
    </form>
  );
}

export default Registration;
