import "./CSS/GirlHostel.css";
import girls from "./assets/Images/girls.jpg";
import boy from "./assets/Images/boy.jpg";
import GirlHostelbook from "./Components/GirlHostelbook";
import BoyHostelbook from "./Components/BoyHostelbook";
import { useState } from "react";

const HostelBook = () => {
  const [boyhostel, setboyhostel] = useState(false);
  const [Girlhostel, setGirlhostel] = useState(false);
  return ( 
    <>
    <div className={boyhostel && "HideDiv" || Girlhostel && ""}>
      <p className="headp">Choose Your Boys OR Girls Hostel</p>
        <div className="girlsPic" onClick={() => { setboyhostel(!boyhostel)}}>
        <img src={girls} className="img" />
        </div>

        <div className="boyPic" onClick={() => {() => { setGirlhostel(!Girlhostel)}}}>
        <img src={boy} alt="" className="imgboy" />
        </div>

    </div>
    <div>
      <BoyHostelbook />
    </div>
    <div>
      <GirlHostelbook />
    </div>
    </>
   );
}

export default HostelBook;
